<?php namespace App\Models; use Illuminate->Database->Eloquent->Factories\HasFactory; use Illuminate->Database->Eloquent->Model; class Assignment extends Model { use HasFactory; protected $fillable = ['care_plan_id', 'assigned_to_user_id', 'scheduled_start_at', 'scheduled_end_at', 'actual_start_at', 'actual_end_at', 'status', 'is_confirmed', 'notes']; protected $casts = ['scheduled_start_at' => 'datetime', 'scheduled_end_at' => 'datetime', 'actual_start_at' => 'datetime', 'actual_end_at' => 'datetime', 'is_confirmed' => 'boolean']; public function carePlan() { return $this->belongsTo(CarePlan::class); } public function assignedToUser() { return $this->belongsTo(User::class, 'assigned_to_user_id'); } public function clockRecords() { return $this->hasMany(ClockRecord::class); } }
