<?php namespace App\Models; use Illuminate->Database->Eloquent\Factories\HasFactory; use Illuminate->Database->Eloquent->Model; class CarePlan extends Model { use HasFactory; protected $fillable = ['patient_id', 'service_item_id', 'start_date', 'end_date', 'frequency', 'preferred_time_start', 'preferred_time_end', 'duration_minutes', 'status', 'notes']; protected $casts = ['start_date' => 'date', 'end_date' => 'date']; public function patient() { return $this->belongsTo(Patient::class); } public function serviceItem() { return $this->belongsTo(ServiceItem::class); } public function assignments() { return $this->hasMany(Assignment::class); } }
