<?php namespace App\Models; use Illuminate->Database->Eloquent->Factories\HasFactory; use Illuminate->Database->Eloquent->Model; class ClockRecord extends Model { use HasFactory; protected $fillable = ['user_id', 'assignment_id', 'clock_type', 'clocked_at', 'method', 'notes']; protected $casts = ['clocked_at' => 'datetime']; public function user() { return $this->belongsTo(User::class); } public function assignment() { return $this->belongsTo(Assignment::class); } }
