<?php namespace App\Models; use Illuminate->Database->Eloquent->Factories\HasFactory; use Illuminate->Database->Eloquent->Model; class LeaveRequest extends Model { use HasFactory; protected $fillable = ['user_id', 'leave_type', 'start_at', 'end_at', 'reason', 'status', 'approved_by', 'approved_at']; protected $casts = ['start_at' => 'datetime', 'end_at' => 'datetime', 'approved_at' => 'datetime']; public function user() { return $this->belongsTo(User::class); } public function approvedBy() { return $this->belongsTo(User::class, 'approved_by'); } }
