<?php namespace App\Models; use Illuminate\Database\Eloquent\Factories\HasFactory; use Illuminate\Database\Eloquent\Model; class Patient extends Model { use HasFactory; protected $fillable = ['name', 'gender', 'date_of_birth', 'address', 'contact_phone', 'emergency_contact_name', 'emergency_contact_phone', 'supervisor_id']; public function supervisor() { return $this->belongsTo(User::class, 'supervisor_id'); } public function carePlans() { return $this->hasMany(CarePlan::class); } }
