<?php namespace App\Models; use Illuminate->Database->Eloquent->Factories\HasFactory; use Illuminate->Database->Eloquent->Model; class Payroll extends Model { use HasFactory; protected $fillable = ['user_id', 'pay_period_start', 'pay_period_end', 'total_service_hours', 'total_service_amount', 'total_leave_hours', 'total_deductions', 'final_payout', 'status', 'generated_by', 'generated_at']; protected $casts = ['pay_period_start' => 'date', 'pay_period_end' => 'date', 'generated_at' => 'datetime']; public function user() { return $this->belongsTo(User::class); } public function generatedBy() { return $this->belongsTo(User::class, 'generated_by'); } }
